#pragma config(Sensor, S2,     leftTouch,           sensorTouch)
#pragma config(Sensor, S3,     rightTouch,          sensorTouch)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//



const int kMaxSizeOfMessage = 5;
const TMailboxIDs kQueueID = mailbox1;

task main()
{
  ubyte nXmitBuffer[kMaxSizeOfMessage] = {0x01, 0x02, 0x03, 0x04, 0x00}; // For NXT-G compatability, last byte of message must be zero because of string messsages.

  setBluetoothOn();
  while(true)
  {
    if (SensorValue(leftTouch)==1)
    {
      PlaySound(soundBeepBeep);
      cCmdMessageWriteToBluetooth(nXmitBuffer,kMaxSizeOfMessage,kQueueID);
      sendMessageWithParm();
    }
  }
}
